import React, { useContext, useState } from 'react';
import taskContext from '../context/tasks/taskContext';

const AddTask = (props) => {
  const context = useContext(taskContext);
  const { addTask } = context;

  const [task, setTask] = useState({
    title: '',
    description: '',
    tag: '',
    duedate: '',
    users: [], // Add an empty array for users
  });

  const handleClick = (e) => {
    e.preventDefault();
    const duedate = new Date(task.duedate);
    const formattedDuedate = duedate.toISOString();

    addTask(task.title, task.description, task.tag, formattedDuedate, task.users); // Include the users field in the addTask function call
    setTask({
      title: '',
      description: '',
      tag: '',
      duedate: '',
      users: [], // Reset the users field
    });
    props.showAlert('Added Successfully', 'success');
  };

  const onChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  return (
    <div className="container my-3">
      <h2>Add a Task</h2>
      <form className="my-3">
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            type="text"
            className="form-control"
            id="title"
            name="title"
            onChange={onChange}
            value={task.title}
            minLength={5}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="description" className="form-label">
            Description
          </label>
          <input
            type="text"
            className="form-control"
            id="description"
            name="description"
            value={task.description}
            onChange={onChange}
            minLength={5}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="tag" className="form-label">
            Tag
          </label>
          <input
            type="text"
            className="form-control"
            id="tag"
            name="tag"
            onChange={onChange}
            value={task.tag}
            minLength={5}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="duedate" className="form-label">
            Due Date
          </label>
          <input
            type="text"
            className="form-control"
            id="duedate"
            name="duedate"
            value={task.duedate}
            onChange={onChange}
            minLength={5}
            required
          />
        </div>

        {/* Add the dropdown field for selecting members */}
        <div className="mb-3">
          <label htmlFor="users" className="form-label">
            Assign Task To
          </label>
          <select
            className="form-select"
            id="users"
            name="users"
            value={task.users}
            onChange={onChange}
            multiple
          >
            {/* Populate the options with available members */}
            {/* Replace `members` with the array of available members */}
            {members.map((member) => (
              <option key={member.id} value={member.id}>
                {member.name}
              </option>
            ))}
          </select>
        </div>

        <button
          disabled={task.title.length < 5 || task.description.length < 5}
          type="submit"
          className="btn btn-primary"
          onClick={handleClick}
        >
          Add Task
        </button>
      </form>
    </div>
  );
};

export default AddTask;
