import { useState } from "react";
import TaskContext from "./taskContext";

const TaskState = (props) => {
    const s1 = {
        "name" : "Harry",
        "class" : "5b"
    }
    const [state, setState] = useState(s1);
    const update = () => {
        setTimeout(() => {
            setState ({
                "name" : "Larry",
                "class" : "10b"
            })
        }, 1000);
    }
    return (
        <TaskContext.Provider value = {{state,update}}>
            {props.children};
        </TaskContext.Provider>
    )
}

export default TaskState;
