import React, { useContext } from "react";
import taskContext from "../context/tasks/taskContext";

const Taskitem = (props) => {
  const context = useContext(taskContext);
  const { deleteTask } = context;
  const { task, updateTask } = props;

  console.log(task);

  const formattedduedate = task.duedate
    ? new Date(task.duedate).toLocaleDateString()
    : "";

    const handleProgressChange = (status) => {
        // Update the progress of the task
        editTask(task._id, task.title, task.description, task.tag, task.duedate, status);
        props.showAlert("Progress updated successfully", "success");
      };


  return (
    <div className="col-md-3">
      <div className="card my-3">
        <div className="card-body">
          <h5 className="card-title">{task.title}</h5>
          <p className="card-text">{task.description}</p>
          <p>Due Date: {formattedduedate}</p>

          <p>Progress: {task.status}</p> {/* Display the progress */}
          <div className="btn-group" role="group">
            <button
              className="btn btn-primary"
              onClick={() => handleProgressChange("not_started")}
            >
              Not Started
            </button>
            <button
              className="btn btn-primary"
              onClick={() => handleProgressChange("in_progress")}
            >
              In Progress
            </button>
            <button
              className="btn btn-primary"
              onClick={() => handleProgressChange("completed")}
            >
              Completed
            </button>
          </div>


          <i
            className="fa-solid fa-trash mx-2"
            onClick={() => {
              deleteTask(task._id);
              props.showAlert("Deleted Successfully", "success");
            }}
          ></i>
          <i
            className="fa-sharp fa-solid fa-pen-to-square mx-2"
            onClick={() => {
              updateTask(task);
            }}
          ></i>
        </div>
      </div>
    </div>
  );
};

export default Taskitem;
