import React, { useContext, useState  } from 'react'; 
import taskContext from '../context/tasks/taskContext';


export const AddTask = () => {

    const context = useContext(taskContext);
    const {addTask} = context;

    const [task,setTask] = useState({title:"",description:"",tag:"default",duedate:"default"})

    const handleClick = (e) => {
        e.preventDefault();
        addTask(task.title, task.description, task.tag, task.duedate);
    }

    const onChange = (e) => {
            setTask({...task,[e.target.name]:e.target.value})
    }

    return (
        <div className="container my-3">
        <h2>Add a Task</h2>
        <form className="my-3">
<div className="mb-3">
<label htmlFor="title" className="form-label">Title</label>
<input type="text" className="form-control" id="title" name="title" onChange={onChange} minLength={5} required/>

</div>

<div className="mb-3">
<label htmlFor="description" className="form-label">Description</label>
<input type="text" className="form-control" id="description" name="description" onChange={onChange} minLength={5} required/>
</div>
<div className="mb-3">
<label htmlFor="tag" className="form-label">Tag</label>
<input type="text" className="form-control" id="tag" name="tag" onChange={onChange} minLength={5} required/>
</div>
<div className="mb-3">
<label htmlFor="duedate" className="form-label">Due Date</label>
<input type="text" className="form-control" id="duedate" name="duedate" onChange={onChange} minLength={5} required/>
</div>


<button disabled type="submit" className="btn btn-primary" onClick={handleClick}>Add Task</button>
</form>
</div>
    )
}

export default AddTask;
