import React, { useContext, useEffect, useRef } from 'react'; 
import Taskitem from './Taskitem';
import AddTask from './AddTask';
import taskContext from '../context/tasks/taskContext';


 const Tasks = () => {
    const context = useContext(taskContext);
    const {tasks, getTasks} = context;
    useEffect(()=> {
        getTasks()
        // eslint-disable-next-line
    },[])

    const updateTask = (task) => {
        ref.current.click(); 
    } 
    const ref = useRef(null);
    return (
        <>
         <AddTask/>

         <button ref={ref} type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
        </button>


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Edit Task</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">

      <form className="my-3">
<div className="mb-3">
<label htmlFor="title" className="form-label">Title</label>
<input type="text" className="form-control" id="etitle" name="etitle" onChange={onChange}/>

</div>

<div className="mb-3">
<label htmlFor="description" className="form-label">Description</label>
<input type="text" className="form-control" id="edescription" name="edescription" onChange={onChange}/>
</div>
<div className="mb-3">
<label htmlFor="tag" className="form-label">Tag</label>
<input type="text" className="form-control" id="etag" name="etag" onChange={onChange}/>
</div>
<div className="mb-3">
<label htmlFor="duedate" className="form-label">Due Date</label>
<input type="text" className="form-control" id="duedate" name="duedate" onChange={onChange}/>
</div>


<button type="submit" className="btn btn-primary" onClick={handleClick}>Add Task</button>
</form>

      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Update Task</button>
      </div>
    </div>
  </div>
</div>
        <div className="row my-3">
        <h2>Your Tasks</h2>
        {tasks.map((task)=>{
        return <Taskitem key={task._id} updateTask={updateTask} task={task}/>
        })}
        </div> 
        </>
    )
}

export default Tasks;
