import React, { useContext } from "react";
import taskContext from "../context/tasks/taskContext";

const Taskitem = (props) => {
  const context = useContext(taskContext);
  const { deleteTask } = context;
  const { task, updateTask } = props;

  const formattedduedate = new Date(task.duedate).toLocaleDateString();


  return (
    <div className="col-md-3">
      <div className="card my-3">
        <div className="card-body">
          <h5 className="card-title">{task.title}</h5>
          <p className="card-text">{task.description}</p>
          <p>Due Date: {formattedduedate}</p>
          <div>
            <button onClick={() => handleStatusChange("not_started")}>Not Started</button>
            <button onClick={() => handleStatusChange("in_progress")}>In Progress</button>
            <button onClick={() => handleStatusChange("completed")}>Completed</button>
          </div>
          <i
            className="fa-solid fa-trash mx-2"
            onClick={() => {
              deleteTask(task._id);
              props.showAlert("Deleted Successfully", "success");
            }}
          ></i>
          <i
            className="fa-sharp fa-solid fa-pen-to-square mx-2"
            onClick={() => {
              updateTask(task);
            }}
          ></i>
        </div>
      </div>
    </div>
  );
};

export default Taskitem;
