// eslint-disable-next-line
import { useState,useEffect } from "react";
import TaskContext, { TaskProvider } from "./taskContext";

const TaskState = (props) => {
  const host = "http://localhost:5500";
  const tasksInitial = [];
  const usersInitial = [];

  const [tasks, setTasks] = useState(tasksInitial);
  const [users, setUsers] = useState(usersInitial);
  const [selectedUsers, setSelectedUsers] = useState([])



// GET all Users
const getUsers = async () => {
  try {
    const response = await fetch(`${host}/api/tasks/fetchallusers`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
    });
    const json = await response.json();
    setUsers(json);
  } catch (error) {
    console.error(error);
  }
};







  useEffect(() => {
    getTasks();
    getUsers();
  }, []);


  // GET all Tasks
  const getTasks = async () => {
    //API call
    const response = await fetch(`${host}/api/tasks/fetchalltasks`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
    });
    const json = await response.json();
    setTasks(json);
  };





  // Add a Task
  const addTask = async (title, description, tag, duedate) => {
    try {
      const response = await fetch(`${host}/api/tasks/addtask`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("token"),
        },
        body: JSON.stringify({
          title,
          description,
          tag,
          duedate,
          users: selectedUsers,
        }),
      });

      const task = await response.json();
      setTasks([...tasks, task]);
    } catch (error) {
      console.error(error);
    }
  };





  // Delete a Task
  const deleteTask = async (id) => {
    // API call
    const response = await fetch(`${host}/api/tasks/deletetask/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
    });

    const json = response.json();
    console.log(json);
    const newTasks = tasks.filter((task) => {
      return task._id !== id;
    });
    setTasks(newTasks);
  };





  // Edit a Task
  const editTask = async (id, title, description, tag, duedate, status) => {
    //API call
    const response = await fetch(`${host}/api/tasks/updatetask/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({ title, description, tag, duedate, status}),
    });

    const json = await response.json();
    console.log(json);
    let newTasks = JSON.parse(JSON.stringify(tasks));

    // Logic to edit in client
    for (let index = 0; index < newTasks.length; index++) {
      const element = newTasks[index];
      if (element._id === id) {
        newTasks[index].title = title;
        newTasks[index].description = description;
        newTasks[index].tag = tag;
        newTasks[index].duedate = duedate;
        newTasks[index].status = status;
        break; 
      }
    }
    setTasks(newTasks);
  };

   // Select User
   const selectUser = (user) => {
    setSelectedUsers((prevSelectedUsers) => [...prevSelectedUsers, user]);
  };

  return (
    <TaskContext.Provider
    value={{
      tasks,
      users,
      addTask,
      deleteTask,
      editTask,
      getTasks,
      getUsers,
      selectUser,
      selectedUsers,
    }}
  >
    {props.children}
  </TaskContext.Provider>

  );
};

export default TaskState;
